import React from 'react';
import { Breadcrumbs, Link, Typography } from '@material-ui/core'
import { useHistory } from 'react-router-dom'

function BreadcrumbNav(props) {
    const { push, location: { pathname } } = useHistory()
    const pathArr = pathname.split("/").filter(x => x)

    return (
        <div>
            <Breadcrumbs aria-label="breadcrumb">
                    {pathArr.length > 0 && 
                        <Link color="inherit" onClick={() => push("/")}>
                            Dashboard
                        </Link>}
                        {pathArr.map((name, i) => {
                            const upperCaseName = name[0].toUpperCase() + name.substring(1)
                            if(i === pathArr.length-1) {
                                return <Typography color="textPrimary" onClick>
                                    {upperCaseName}
                                </Typography>
                            }
                            const routeTo = `/${pathArr.slice(0, i+1).join("/")}`
                            return <Link color="inherit" onClick={() => push(routeTo)}>
                                {upperCaseName}
                            </Link>
                        })
                    }
            </Breadcrumbs>
        </div>
    );
}

export default BreadcrumbNav;
