import React, { useContext, useEffect } from 'react';
import { Typography, Grid, Paper, makeStyles } from '@material-ui/core'

import BreadcrumbNav from '../layout/BreadcrumbNav'
import { GroupContext } from '../../context/GroupProvider';
import { Droppable, Draggable } from 'react-beautiful-dnd';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
    },
    subPaper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        backgroundColor: theme.palette.background.subPaper
    },
    subtitle: {
        color: "#adb0bb",
        fontSize: "0.85em"
    }
}));

function Groups(props) {
    const classes = useStyles()
    const { groups, getAllGroups } = useContext(GroupContext)

    useEffect(() => {
        getAllGroups()
    }, [])

    return (
        <div>
            <BreadcrumbNav />
            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <Typography variant="h4">Student Groups</Typography>
                </Grid>
                {groups?.map(group => (
                    <Droppable droppableId="droppable-1" type="PERSON" key={group._id}>
                    {(provided, snapshot) => (
                        <Grid item xs={12} sm={12/groups.length}>
                        <Paper
                            ref={provided.innerRef}
                            className={classes.paper}
                            // style={{ backgroundColor: snapshot.isDraggingOver ? 'blue' : 'grey' }}
                            {...provided.droppableProps}
                        >
                            <Typography variant="h6">{group.name}</Typography>
                            {provided.placeholder}
                            <Draggable draggableId={group.name} index={0}>
                            {(provided, snapshot) => (
                                <Paper 
                                    className={classes.subPaper}
                                    ref={provided.innerRef}
                                    {...provided.draggableProps}
                                    {...provided.dragHandleProps}
                                >

                                </Paper>
                            )}
                            </Draggable>
                        </Paper>
                        </Grid>
                    )}
                    </Droppable>
                ))}
            </Grid>
        </div>
    );
}

export default Groups;
