import { createMuiTheme } from '@material-ui/core';

const darkTheme = {
    palette: {
        background: {
            default: "#1c2025"
        },
        primary: {
            main: "#282C34",
        },
        secondary: {
            main: "#8a85ff",
        }
    },
    typography: {
        fontFamily: [
          '-apple-system',
          'BlinkMacSystemFont',
          '"Segoe UI"',
          'Roboto',
          '"Helvetica Neue"',
          'Arial',
          'sans-serif',
          '"Apple Color Emoji"',
          '"Segoe UI Emoji"',
          '"Segoe UI Symbol"',
        ].join(','),
      }
};
const lightTheme = {
    palette: {
        background: {
            default: "#fefefe"
        },
        primary: {
            main: "#eee",
        },
        secondary: {
            main: "#8a85ff",
        }
    },
};

export { darkTheme, lightTheme }
