import React from 'react';
import { NavLink } from 'react-router-dom'
import { Drawer, Toolbar, List, ListItem, ListItemText, ListItemIcon, makeStyles } from '@material-ui/core';
import DashboardIcon from '@material-ui/icons/Dashboard';
import GroupIcon from '@material-ui/icons/Group';
import TimelineIcon from '@material-ui/icons/Timeline';

const drawerWidth = 240;
const useStyles = makeStyles((theme) => ({
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
    },
        drawerPaper: {
        marginTop: 64,
        width: drawerWidth,
    },
    toolbar: {
        padding: 0,
    },
    list: {
        width: drawerWidth,
    },
    listItem: {
        textDecoration: "none",
        '&:hover': {
            backgroundColor: "rgba(0,0,0,0.15)"
        }
    },
    icon: {
    }
}));


function SideDrawer(props) {
    const classes = useStyles()

    const icons = {
        Progress: <TimelineIcon color="secondary" />,
        Groups: <GroupIcon color="secondary" />,
        Dashboard: <DashboardIcon color="secondary" />
    }

    const list1 = [
        {text: 'Dashboard', path: "/"},
        {text: 'Groups', path: "/groups"},
        {text: 'Progress', path: "/progress"}
    ]

    return (
        <div>
            <Drawer
                variant="permanent"
                anchor={'left'}
                classes={{
                paper: classes.drawerPaper,
                }}
            >
                <Toolbar className={classes.toolbar}>
                    <List className={classes.list}>
                        {list1.map((item, index) => (
                            <NavLink 
                                key={item.text} 
                                to={item.path}
                                className={classes.listItem}
                            >  
                                <ListItem 
                                    button
                                    className={classes.listItem}
                                >
                                    <ListItemIcon>{icons[item.text]}</ListItemIcon>
                                    <ListItemText primary={item.text} />
                                </ListItem>
                            </NavLink>
                        ))}
                    </List>
                </Toolbar>
            </Drawer>
        </div>
    );
}

export default SideDrawer;
