import React, { useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom'
import { StudentContext } from '../../context/StudentProvider'
import { Typography, Grid, Paper, makeStyles } from '@material-ui/core'

import BreadcrumbNav from '../layout/BreadcrumbNav'

function StudentPage(props) {
    const { studentId } = useParams()
    const { student: {firstName, lastName}, getStudent } = useContext(StudentContext)

    useEffect(() => {
        getStudent(studentId)
    }, [])

    const useStyles = makeStyles((theme) => ({
        root: {
            flexGrow: 1,
        },
        paper: {
            padding: theme.spacing(2),
            textAlign: 'center'
        },
        subPaper: {
            padding: theme.spacing(2),
            textAlign: 'center',
            backgroundColor: theme.palette.background.subPaper,
            margin: "20px 0",
            transition: "0.2s ease",
            '&:hover': {
                transform: "scale(1.01)",
                filter: "brightness(0.9)"
                // backgroundColor: theme.palette.background.subPaperHover
            }
        },
        subtitle: {
            color: "#adb0bb",
            fontSize: "0.85em"
        },
    }));


    return (
        <div>
            <BreadcrumbNav student={`${firstName} ${lastName}`} />
            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <Typography variant="h4">{firstName} {lastName}</Typography>
                    <Typography variant="subtitle1">MODULE 1</Typography>
                    <Typography variant="subtitle1">PROGRESS POINT 44.5</Typography>
                </Grid>
            </Grid>
        </div>
    );
}

export default StudentPage;
