import React, { useContext, useEffect } from 'react';
import { Typography, Grid, Paper, makeStyles } from '@material-ui/core'
import { Droppable, Draggable } from 'react-beautiful-dnd';

import BreadcrumbNav from '../layout/BreadcrumbNav'
import { GroupContext } from '../../context/GroupProvider';
import { StudentContext } from '../../context/StudentProvider';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
    },
    subPaper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        backgroundColor: theme.palette.background.subPaper
    },
    subtitle: {
        color: "#adb0bb",
        fontSize: "0.85em"
    }
}));

function Groups(props) {
    const classes = useStyles()
    const { groups, getAllGroups } = useContext(GroupContext)
    const { students, getAllStudents } = useContext(StudentContext)

    useEffect(() => {
        getAllGroups()
        getAllStudents()
    }, [])

    return (
        <div>
            <BreadcrumbNav />
            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <Typography variant="h4">Student Groups</Typography>
                </Grid>
                {groups?.map(group => (
                    <Droppable droppableId="droppable-1" type="PERSON" key={group._id}>
                    {(provided, snapshot) => (
                        <Grid item xs={12} sm={12/groups.length}>
                        <Paper
                            ref={provided.innerRef}
                            className={classes.paper}
                            // style={{ backgroundColor: snapshot.isDraggingOver ? 'blue' : 'grey' }}
                            {...provided.droppableProps}
                        >
                            <Typography variant="h6">{group.name}</Typography>
                            {provided.placeholder}
                            {students.map(student => {
                                if(student.group === group._id){
                                    studentIndex++
                                    console.log(studentIndex)
                                    return (
                                        <Draggable 
                                            key={student._id} 
                                            draggableId={student._id} 
                                            index={studentIndex}
                                        >
                                        {(provided, snapshot) => (
                                            <Paper 
                                                className={classes.subPaper}
                                                ref={provided.innerRef}
                                                {...provided.draggableProps}
                                                {...provided.dragHandleProps}
                                            >

                                            </Paper>
                                        )}
                                        </Draggable>
                                )
                                }
                            })}
                        </Paper>
                        </Grid>
                    )}
                    </Droppable>
                ))}
            </Grid>
        </div>
    );
}

export default Groups;
