import React, { useState } from 'react'
import './App.css';
import { Switch, Route } from 'react-router-dom'
import { ThemeProvider, createMuiTheme, CssBaseline, makeStyles } from '@material-ui/core';
import { darkTheme, lightTheme } from './themes'

import { Topbar } from './components/layout'
// import { Topbar, SideDrawer } from './components/layout'
// import { Dashboard, Groups, StudentPage } from './components/pages'

function App() {
    const [theme, setTheme] = useState("light")

    const light = createMuiTheme(lightTheme)
    const dark = createMuiTheme(darkTheme)

    function toggleTheme() {
        setTheme(prev => prev === "light" ? "dark" : "light")
    }

    const useStyles = makeStyles((theme) => ({
        content: {
          flexGrow: 1,
          padding: theme.spacing(3),
          textAlign: "left",
          marginLeft: 240
        }
    }));
    const classes = useStyles()

    return (
        <ThemeProvider theme={theme === "light" ? light : dark}>
        <div className="App">
        <CssBaseline />

            <Topbar toggleTheme={toggleTheme} />
            <SideDrawer />
            <div className={classes.content}>
                <Switch>
                    <Route exact path="/">
                        <Dashboard />
                    </Route>
                    <Route path="/groups">
                        <Groups />
                    </Route>
                    <Route path="/students/:studentId">
                        <StudentPage />
                    </Route>
                </Switch>
            </div>
        </div>
        </ThemeProvider>
    );
}

export default App;
