import React from 'react';
import { useHistory } from 'react-router-dom'
import { Drawer, Toolbar, List, ListItem, ListItemText, ListItemIcon, makeStyles } from '@material-ui/core';
import DashboardIcon from '@material-ui/icons/Dashboard';
import GroupIcon from '@material-ui/icons/Group';
import TimelineIcon from '@material-ui/icons/Timeline';

const drawerWidth = 240;
const useStyles = makeStyles((theme) => ({
    drawer: {
        width: drawerWidth,
        flexShrink: 0,
    },
        drawerPaper: {
        marginTop: 64,
        width: drawerWidth,
    },
    toolbar: {
        padding: 0,
    },
    list: {
        width: drawerWidth,
    },
    listItem: {
        textDecoration: "none",
        '&:hover': {
            backgroundColor: "rgba(0,0,0,0.15)"
        }
    },
    icon: {
    }
}));


function SideDrawer(props) {
    const classes = useStyles()

    const list1 = [
        {text: 'Dashboard', path: "/", icon: <DashboardIcon color="secondary" />},
        {text: 'Groups', path: "/groups", icon: <GroupIcon color="secondary" />},
        {text: 'Progress', path: "/progress", icon: <TimelineIcon color="secondary" />}
    ]

    function handleClick(path) {
        const { push, location: { pathname } } = useHistory()
        push(path)
    }

    return (
        <div>
            <Drawer
                variant="permanent"
                anchor={'left'}
                classes={{
                paper: classes.drawerPaper,
                }}
            >
                <Toolbar className={classes.toolbar}>
                    <List className={classes.list}>
                        {list1.map(item => {
                            const { text, path, icon } = item
                            return (
                                <ListItem 
                                    button key={text}
                                    onClick={() => handleClick(path)}
                                    selected={path === pathname}
                                >
                                    <ListItemIcon>{icon}</ListItemIcon>
                                    <ListItemText primary={text} />
                                </ListItem>)
                        })}
                    </List>
                </Toolbar>
            </Drawer>
        </div>
    );
}

export default SideDrawer;
