import React, { useContext, useEffect } from 'react';
import { Typography, Grid, Paper, makeStyles } from '@material-ui/core'

import BreadcrumbNav from '../layout/BreadcrumbNav'
import { GroupContext } from '../../context/GroupProvider';
import { Droppable } from 'react-beautiful-dnd';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
    },
    subtitle: {
        color: "#adb0bb",
        fontSize: "0.85em"
    }
}));

function Groups(props) {
    const classes = useStyles()
    const { groups, getAllGroups } = useContext(GroupContext)

    useEffect(() => {
        getAllGroups()
    }, [])

    return (
        <div>
            <BreadcrumbNav />
            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <Typography variant="h4">Student Groups</Typography>
                </Grid>
                {groups?.map(group => (
                    <Droppable key={group._id}  droppableId="droppable-1">
                        <Grid item xs={3}>
                        <Paper className={classes.paper}>
                            <Typography 
                                variant="h6" 
                            >{group.name}</Typography>
                        </Paper>
                        </Grid>
                    </Droppable>
                ))}
            </Grid>
        </div>
    );
}

export default Groups;
