import React from 'react';
import { Breadcrumbs, Link, Typography } from '@material-ui/core'
import { useHistory } from 'react-router-dom'

function BreadcrumbNav(props) {
    const { history, location: { pathname } } = useHistory()
    const pathArr = pathname.split("/").filter(x => x)

    function handleRoute(i) {
        if(i === null) history.push("/")
        const routeTo = `/${pathArr.slice(0, i+1).join("/")}`
        history.push(routeTo)
    }

    return (
        <div>
            <Breadcrumbs aria-label="breadcrumb">
                    {pathArr.length > 0 && 
                    <Link color="inherit" onClick={handleRoute}>
                        Dashboard
                    </Link>}
                {pathArr.map((name, i) => {
                    const upperCaseName = name[0].toUpperCase() + name.substring(1)
                    if(i === pathArr.length-1) {
                        return <Typography color="textPrimary" onClick>
                            {upperCaseName}
                        </Typography>
                    }
                    return <Link color="inherit" onClick={handleRoute}>
                        {upperCaseName}
                    </Link>
                })}
            </Breadcrumbs>
        </div>
    );
}

export default BreadcrumbNav;
